import React, {useState} from 'react';
import {GoogleMap, withScriptjs, withGoogleMap, Marker, InfoWindow} from 'react-google-maps';
import InfoBox from 'react-google-maps/lib/components/addons/InfoBox';

let CurrentLat;
let CurrentLng;

// const getLocation = (position) =>{
//     CurrentLat = position.coords.latitude;
//     CurrentLng = position.coords.longitude;
//     console.log(CurrentLat);
//     console.log(CurrentLng);
// }

// navigator.geolocation.getCurrentPosition(getLocation, (err) => {console.log(err)});

function myMap(){
    const [selection, setSelection] = useState(null);

    return(
        <GoogleMap 
        defaultZoom={10}
        defaultCenter={{lat:45.421532, lng:-75.697189}}>
        <Marker key='#123456' 
        position={{lat:45.421532, lng:-75.697189}}
        onClick={setSelection(this)}/>
        {setSelection && (
            <InfoWindow>
                <div>Details</div>
            </InfoWindow>
        )}
        </GoogleMap>
    );
}

const WrappedMap = withScriptjs(withGoogleMap(myMap));

const MapWindow = ()=>{
    return (
        <div style={{width: '350px', height: '350px'}}>
            <WrappedMap 
                googleMapURL={"https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=AIzaSyDy6UiT7wnGpImKxRge49xNpSdxTVWWQe8"}
                loadingElement={<div style={{ height: `100%` }} />}
                containerElement={<div style={{ height: `100%` }} />}
                mapElement={<div style={{ height: `100%` }} />}
                />
        </div>
    )
}

export default MapWindow;
