import React, { useEffect, useState } from 'react';
import TopBar from '../Components/MainPage/TopBar';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Axios from 'axios';

const axios = Axios.create()

const useStyle = makeStyles(theme => ({
    footer: {
        height: '8vh',
        width: '100vw',
        textAlign: 'center',
        alignItems: 'center',
        backgroundColor: '#fa877f',
        color: '#FFFFFF',
        fontSize: '2vh',
        display: 'fixed',
        bottom: '0px'
    },
    solid: {
        display: 'block',
        width: '100%',
        height: '64px',
        backgroundColor: '#FFFFFF'
    },
    container: {
        height: '90vh',
        overflow: 'scroll',
        flexDirection: 'column',
        maxWidth: '700px',
    },
    userTitle: {
        display: 'flex',
        width: '100%',
        height: '15vh',
        color: '#5c5757',
        backgroundColor: '#f4f4f4',
        flexDirection: 'row',
    },
    userInfo: {
        paddingLeft: '5%',
        paddingTop: '2%',
        margin: '0px'
    },
    partition: {
        display: 'flex',
        flexDirection: 'column',
        width: '50%',
    },
    img: {
        display: 'flex'
    }
}))

const UserProfilePage = (props) => {
    const [img, setImg] = useState(null)

    useEffect(() => {
        axios.get("https://randomuser.me/api/")
            .then(res => {
                setImg(res.data.results[0].picture.large)
            })
    }, [])

    const classes = useStyle()
    return (
        <div>
            <TopBar />
            <div className={classes.solid}></div>
            <br />
            <Container className={classes.container}>
                <div className={classes.userTitle}>
                    <div className={classes.partition}>
                        <h1 className={classes.userInfo}>John Doe</h1>
                        <small className={classes.userInfo}>yumming since: 2019-10-09</small>
                    </div>
                    <div className={classes.partition}>
                        <img src={img} width="5vh"></img>
                    </div>
                </div>
                <h2>My reviews</h2>
            </Container>
            <div className={classes.footer}>
                <p>Copyright Â© 2019-03-12 Yummy Inc.</p>
            </div>
        </div>
    )
}

export default UserProfilePage;
