import React, { useState, useEffect} from 'react';
import Container from '@material-ui/core/Container';
import { makeStyles } from '@material-ui/core/styles';
import BusinessTitle from './BusinessTitle';
import Button from '@material-ui/core/Button';
import Ratings from '../DetailPage/Rating';
import Gallery from '../MainPage/Gallery';
import Link from '@material-ui/core/Link';
import Axios from 'axios';

const axios = Axios.create()

const useStyle = makeStyles(theme =>({
    container:{
        height: '90vh',
        overflow: 'scroll',
        flexDirection:'column',
        maxWidth: '700px',
    },
    h3:{
        color: '#5c5757'
    },
    review:{
        width: '100%',
        height: 'auto',
        backgroundColor:'#f4f4f4',
        boxShadow: '5px 5px 10px -5px #aaaaaa',
        paddingBottom:'5px',
        marginTop:'20px'
    },
    reviewTitle:{
        fontWeight:'bold',
        fontSize:'23px',
        paddingTop: '10px',
        margin: '5px'
    },
}))

const ReviewList = (props) => {
    const classes = useStyle()

    useEffect(()=>{
        axios.get(`https://pyrite-peanut.glitch.me/api/business/id/${props.businessId}`)
        .then(res=>{
            localStorage.setItem("Blist",JSON.stringify(res.data))
            console.log(JSON.parse(localStorage.getItem("Blist")))
            })
        .catch(err=>console.log(err))
    })
    const hours = {
        "Monday": "10:00-21:00",
        "Tuesday": "10:00-21:00",
        "Friday": "10:00-21:00",
        "Wednesday": "10:00-21:00",
        "Thursday": "10:00-21:00",
        "Sunday": "11:00-18:00",
        "Saturday": "10:00-21:00",
    }

    const onClickHandler = (e) => {
        window.location.href='/review/a/a'
    }

    return (
        <Container className={classes.container}>
            <BusinessTitle name="Hello" 
            reviews='9710 reviews'
            address='475 3rd St'
            city='San Francisco'
            state='CA'
            postal='94107'
            hours={hours}/>
            <br/>
            <h2 className={classes.h3}>Recommended Reviews</h2>
            <Button variant='contained' color='secondary' onClick={onClickHandler}>Write a review</Button>
            <br/>
            {
                [{name:'John Doe', 
                stars: 4, 
                date: "2016-03-09", 
                "text": "Great place to hang out after work: the prices are decent, and the ambience is fun. It's a bit loud, but very lively. The staff is friendly, and the food is good. They have a good selection of drinks."
                },{name:'John Doe', 
                stars: 4, 
                date: "2016-03-09", 
                "text": "Great place to hang out after work: the prices are decent, and the ambience is fun. It's a bit loud, but very lively. The staff is friendly, and the food is good. They have a good selection of drinks."
                }].map(r => (
                    <div className={classes.review}>
                        <Container>
                            <h3 className={classes.reviewTitle}>{r.name}</h3>
                            <Ratings star='4' size='small'/>
                            <small>{`posted on ${r.date}`}</small>
                            <p>{r.text}</p>
                        </Container>
                    </div>
                ))
            }
        </Container>
    )
}

export default ReviewList;
