import React, {Component} from 'react';
class Overlays extends Component{
    state = {
        containerClass : "overlay-container",
        triggered: true,
        title: "Hello Friend",
        p:"start your journey with us",
        btn: "Sign In"
    }

    transHandler = () => {
        this.setState((triggered) => {
            console.log(triggered);
            if (triggered == true)
                return {
                    containerClass : "overlay-container panel-active",
                    triggered: true,
                    title: "Welcome Back",
                    p: "stay connected with us",
                    btn: "Sign Up"
                }

            // else {
            //     return {
            //         containerClass : "overlay-container",
            //         triggered: false,
            //         title: "Hello Friend",
            //         p:"start your journey with us",
            //         btn: "Sign In"
            //     }
            // }
        })
    }

    render(){
        return(
        <div className={this.state.containerClass}>
          <div className='overlay'>

            <div className="overlay-panel">
                <h1>{this.state.title}</h1>
                <p>
                    {this.state.p}
                </p>
                <button class="ghost" onClick={() => {this.transHandler(this.state.triggered)}}>{this.state.btn}</button>
            </div>

            {/* <div className='overlay-panel overlay-right'>
                <h1>Hello Friend!</h1>
                <p>
                    start your journey with us
                </p>
                <button class="ghost" id="signUp" onClick={this.transHandler}>Sign Up</button>
            </div> */}

          </div>
        </div>
        );
    }
}

export default Overlays;
