import React from 'react';
import Container from '@material-ui/core/Container';
import { makeStyles } from '@material-ui/core/styles';
import BusinessTitle from './BusinessTitle';
import Button from '@material-ui/core/Button';
import Ratings from '../DetailPage/Rating';
import Gallery from '../MainPage/Gallery';
import Link from '@material-ui/core/Link';

const useStyle = makeStyles(theme =>({
    container:{
        height: '90vh',
        overflow: 'scroll',
        flexDirection:'column',
        maxWidth: '700px',
    },
    h3:{
        color: '#5c5757'
    },
    review:{
        width: '100%',
        height: 'auto',
        marginTop: '20px',
        backgroundColor:'#f4f4f4',
        boxShadow: '5px 5px 10px -5px #aaaaaa',
    },
    reviewTitle:{
        fontWeight:'bold',
        fontSize:'23px'
    },
    reviewContent:{
        flexDirection:'row',
        padding:'3%'
    },
    rating:{
        display: 'flex'
    },
    date:{
        display:'flex'
    }
}))

const ReviewList = (props) => {
    const classes = useStyle()
    const hours = {
        "Monday": "10:00-21:00",
        "Tuesday": "10:00-21:00",
        "Friday": "10:00-21:00",
        "Wednesday": "10:00-21:00",
        "Thursday": "10:00-21:00",
        "Sunday": "11:00-18:00",
        "Saturday": "10:00-21:00",
    }
    return (
        <Container className={classes.container}>
            <BusinessTitle name='Coconuts Caribbean Restaurant & Bar' 
            reviews='9710 reviews'
            address='475 3rd St'
            city='San Francisco'
            state='CA'
            postal='94107'
            hours={hours}/>
            <br/>
            <h2 className={classes.h3}>Recommended Reviews</h2>
            <Button variant='contained' color='secondary'>Write a review</Button>
            <br/>
            {
                [{name:'John Doe', 
                stars: 4, 
                date: "2016-03-09", 
                "text": "Great place to hang out after work: the prices are decent, and the ambience is fun. It's a bit loud, but very lively. The staff is friendly, and the food is good. They have a good selection of drinks."
                },{name:'John Doe', 
                stars: 4, 
                date: "2016-03-09", 
                "text": "Great place to hang out after work: the prices are decent, and the ambience is fun. It's a bit loud, but very lively. The staff is friendly, and the food is good. They have a good selection of drinks."
                }].map(r => (
                    <div className={classes.review}>
                            <h3 className={classes.reviewTitle}>{r.name}</h3>
                            <Ratings star='4' size='small'/>
                            <span>{r.date}</span>
                            <p>{r.text}</p>
                    </div>
                ))
            }
        </Container>
    )
}

export default ReviewList;
