import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import MapWindow from './MapWindow';
import Rating from './Rating';
import Gallery from '../MainPage/Gallery';

const useStyle = makeStyles(theme=>({
    container: {
        flexDirection: 'column',
        alignItems: 'center'
    },
    h1:{
        [theme.breakpoints.down('sm')]:{
            fontSize: '4vh'
        }
    },
    reviews:{
        width: '100%',
        display: 'flex',
        flexDirection: 'row'
    },
    span:{
        alignItems: 'center'
    },
    info:{
        display:'flex',
        flexDirection:'row'
    },
    locations:{
        display: 'flex',
        flexDirection:'column',
        width: '50%',
        margin: '20px'
    },
    hours:{
        display: 'flex',
        flexDirection:'column',
        width: '50%'
    },
    items:{
        display:'flex',
        margin:'0px'
    }
}))

const BusinessTitle = (props) => {
    const classes = useStyle();
    return (
        <div className={classes.container}>
            <div>
                <h1 className={classes.h1}>{props.name}</h1>
                <div className={classes.reviews}>
                    <Rating star='4'/>
                    <span className={classes.span}>{props.reviews}</span>
                </div>
            </div>
            <br/>
            <MapWindow />
            <h3>Locations & Hours</h3>
            <div className={classes.info}>
                <div className={classes.locations}>
                    <p className={classes.items}>{props.address}</p>
                    <p className={classes.items}>{props.city}</p>
                    <p className={classes.items}>{props.state}</p>
                    <p className={classes.items}>{props.postal}</p>
                </div>
                <div className={classes.hours}>
                    <p className={classes.items}>{`Monday: ${props.hours.Monday}`}</p>
                    <p className={classes.items}>{`Tuesday: ${props.hours.Tuesday}`}</p>
                    <p className={classes.items}>{`Wednesday: ${props.hours.Wednesday}`}</p>
                    <p className={classes.items}>{`Thursday: ${props.hours.Thursday}`}</p>
                    <p className={classes.items}>{`Friday: ${props.hours.Friday}`}</p>
                    <p className={classes.items}>{`Saturday: ${props.hours.Saturday}`}</p>
                    <p className={classes.items}>{`Sunday: ${props.hours.Sunday}`}</p>
                </div>
            </div>
        </div>
    )
}

export default BusinessTitle;
