import React from 'react';
import { makeStyles } from '@material-ui/core/styles';


const Rating = (props) => {

    const useStyle = makeStyles(theme=>({
        stars:{
            display: 'flex',
            width: `${props.size.widthS}125px`,
            height: `${props.size.heightS}22px`,
            [theme.breakpoints.up('sm')]:{
                width: `${props.size.widthL}180px`,
                height: `${props.size.heightL}31px`,
            },
            background: "url(" + require(`../../static/rating/${props.size.name}_${props.star}.png`) + ")",
            backgroundSize: 'cover',
            marginRight: '3%'
        },
    }))

    const classes = useStyle(props)
    return (
        <div className={classes.stars}></div>
    )
}

export default Rating;
