import React from 'react'
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/styles';
// import Restaurant from '../../../'

const useStyles = makeStyles(theme => ({
    title:{
        height: '10vw',
        fontSize: '2vw',
        fontWeight: 'bold',
        color: '#fa877f'
    },
    desc:{
        fontSize: '1vw'
    },
    image:{
        paddingTop: '9%',
        width: '50%',
    },
    container: {
        height: '35vw',
        backgroundColor: '#f7f7f7',
        width: '100%',
    },
    item: {
        maxWidth: '180px',
        maxHeight: '180px',
        width: '20vw',
        height: '20vw'
    },
    paper: {
        height: '100%',
        width: '100%',
        backgroundColor: '#FFFFFF',
        border: 'solid 1px #cccccc',
        borderRadius: '5px',
        alignItems: 'center',
        justifyContent: 'center',
        textAlign: 'center'
    },
}))

const Category = () => {
    const classes = useStyles()
    return (
            <Grid container spacing={2} class={classes.container}>
                    <Grid container justify="center" alignContent='center' spacing={2} >
                        <Grid container justify="center" alignContent='center' className={classes.title}>
                        Browse Businesses by Category
                        </Grid>
                        {['Restaurants', 'Shopping', 'Beauty & Spas', 'Active Life', 'More'].map(value => (
                            <Grid key={value} item className={classes.item} justify="center" alignContent='center'>
                                <div className={classes.paper}>
                                    <p className={classes.desc}>{value}</p>
                                    <img src={require(`../../../static/icons/${value}.png`)} className={classes.image}/>
                                </div>
                            </Grid>
                        ))}
                    </Grid>
            </Grid>)
}

export default Category;
