import React, {useState, useEffect} from 'react'
import Pagination from '../Components/SearchPage/Pagination';
import TopBar from '../Components/MainPage/TopBar';
import ResultList from '../Components/SearchPage/ResultList';
import { makeStyles } from '@material-ui/core/styles';


const useStyle = makeStyles(theme =>({
    footer: {
        height: '8vh',
        width: '100vw',
        textAlign: 'center',
        alignItems: 'center',
        backgroundColor: '#fa877f',
        color: '#FFFFFF',
        fontSize: '2vh',
    },
    solid: {
        display: 'block',
        width: '100%',
        height: '64px',
        backgroundColor: '#FFFFFF'
    }
}))

export const SearchPage = (props) => {
    const [logged, setLog] = useState(false);
    const [images, setImage] = useState('');
    const classes = useStyle();

    useEffect(async () => {
        let photo = [];
        let photoId = await axios.get('http://10.215.222.72:4000/api/photoId/50Anorn0DJXFhBr9a9_gHQ');
        console.log(photoId.data);
        photoId.data.photos.forEach(id=>{
            axios.get(`http://10.215.222.72:4000/api/photo/${id}`, 
            {
                responseType: "arraybuffer",
            })
            .then(res=>{
                photo.push('data:image/jpg;base64,' + Buffer.from(res.data, 'binary').toString('base64'));
                setImage(photo[0])
            })
        })
        // console.log(photoId.data.photos[0])
    }, [])

    return (
        <div>
            <TopBar logged={logged} setLog={setLog}/>
            <div className={classes.solid}></div>
            <img src={images}/>
            <ResultList/>
            {/* <Pagination perPage={10} totalPages={100} paginate={()=>{console.log('Hi')}}/> */}
            <div className={classes.footer}>
                <p>Copyright Â© 2019-03-12 Yummy Inc.</p>
            </div>
        </div>
    )
}

export default SearchPage;
