import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import MapWindow from './MapWindow';
import Rating from './Rating';
import Gallery from '../MainPage/Gallery';

const useStyle = makeStyles(theme=>({
    container: {
        flexDirection: 'column',
        alignItems: 'center'
    },
    h1:{
        [theme.breakpoints.down('sm')]:{
            fontSize: '4vh'
        }
    },
    reviews:{
        width: '100%',
        display: 'flex',
        flexDirection: 'row'
    },
    span:{
        alignItems: 'center'
    },
    info:{
        display:'flex',
        flexDirection:'column'
    },
    locations:{
        display: 'flex',
        flexDirection:'column',
        width: '50%'
    },
    hours:{
        display: 'flex',
        width: '50%'
    },
    items:{
        display:'flex',
        margin:'0px'
    }
}))

const BusinessTitle = (props) => {
    const classes = useStyle();
    return (
        <div className={classes.container}>
            <div>
                <h1 className={classes.h1}>{props.name}</h1>
                <div className={classes.reviews}>
                    <Rating star='4'/>
                    <span className={classes.span}>{props.reviews}</span>
                </div>
            </div>
            <br/>
            <MapWindow />
            <h3>Locations & Hours</h3>
            <div className={classes.info}>
                <div className={classes.locations}>
                    <p className={classes.items}>{props.address}</p>
                    <p className={classes.items}>{props.city}</p>
                    <p className={classes.items}>{props.state}</p>
                    <p className={classes.items}>{props.postal}</p>
                </div>
                <div className={classes.hours}>
                    <p className={classes.items}>{props.hours.Monday}</p>
                    <p className={classes.items}>{props.hours.Tuesday}</p>
                    <p className={classes.items}>{props.hours.Wednesday}</p>
                    <p className={classes.items}>{props.hours.Thursday}</p>
                    <p className={classes.items}>{props.hours.Friday}</p>
                    <p className={classes.items}>{props.hours.Saturday}</p>
                    <p className={classes.items}>{props.hours.Sunday}</p>
                </div>
            </div>
        </div>
    )
}

export default BusinessTitle;
