import React from 'react'
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';

const useStyle = makeStyles(theme=>({
    box: {
        paddingTop: '15px',
    },
    title:{
        height: '10vw',
        fontSize: '2vw',
        fontWeight: 'bold',
        [theme.breakpoints.up('md')]:{
            paddingTop: '4%',
        },
        color: '#fa877f',
    },
    desc:{
        fontSize: '1vw'
    },
    image:{
        paddingTop: '8%',
        width: '50%',
    },
    container: {
        height: '30vw',
        maxHeight: '45vh',
        minHeight: '20vh',
        backgroundColor: '#ffffff',
        width: '100%',
    },
    item: {
        maxWidth: '180px',
        maxHeight: '180px',
        width: '20vw',
        height: '20vw'
    },
    paper: {
        height: '100%',
        width: '100%',
        backgroundColor: '#FFFFFF',
        border: 'solid 1px #cccccc',
        borderRadius: '5px',
        alignItems: 'center',
        justifyContent: 'center',
        textAlign: 'center'
    },
}));

const Category = () => {
    const classes = useStyle()
    return (
        <div>
            <Grid container spacing={2} class={classes.container}>
                    <Grid container justify="center" alignContent='center' spacing={2} >
                        <Grid container justify="center" alignContent='center' className={classes.title}>
                        Browse Businesses by Category
                        </Grid>
                        {['Restaurants', 'Shopping', 'Beauty & Spas', 'Active Life'].map(value => (
                            <Grid key={value} item className={classes.item} justify="center" alignContent='center'>
                                <div className={classes.paper}>
                                    <p className={classes.desc}>{value}</p>
                                    <img src={require(`../../static/icons/${value}.png`)} className={classes.image}/>
                                </div>
                            </Grid>
                        ))}
                    </Grid>
            </Grid>
        </div>)
}

export default Category;
