import React, {Component} from 'react';
import Axios from 'axios';
class Signup extends Component{

    reg = new RegExp("^[a-z0-9]+([._\\-]*[a-z0-9])*@([a-z0-9]+[-a-z0-9]*[a-z0-9]+.){1,63}[a-z0-9]+$");
    state = {
        name: "",
        password: "",
        email: "",
    }

    nameHandler(e){
        this.setState({
            name: e.target.value
        })
    }

    emailHandler(e){
        this.setState({
            email: e.target.value
        })
    }

    passwordHandler(e){
        this.setState({
            password: e.target.value
        })
    }

    submitHandler(){
        if (this.state.name === "" || this.state.email === "" || this.state.password === ""){
            alert("Please fill all required fields !")
        }
        else if (!this.reg.test(this.state.email)){
            alert("Please input a valid email address !")
        }
        else {
            const axios = Axios.create();
            axios.post('MyAPI', this.state)
            .then(res => {
                if (res.status == 200){
                    console.log('redirect to main page');
                }
                else {
                    console.log('redirect to error page');
                }
            })
        }
    }

    render(){
        return (
            <div className="signup-container form-container">
                <form action='#'>
                    <h1>Sign Up</h1>
                    <div className="social-container">
                        <a href="#" className="social"><i className="fa fa-facebook-official"></i></a>
                        <a href="#" className="social"><i className="fa fa-google"></i></a>
                        <a href="#" className="social"><i className="fa fa-github"></i></a>
                    </div>
                    <span>Sign up your email for registration</span>
                    <input type="text" placeholder="Name" value={this.state.name} onChange={this.nameHandler}/>
                    <input type="email" placeholder="Email" value={this.state.email} onChange={this.emailHandler}/>
                    <input type="password" placeholder="Password" value={this.state.password} onChange={this.passwordHandler}/>
                    <button onClick={this.submitHandler}>Sign Up</button>
                </form>
            </div>
        );
    }
}

export default Signup;
