import React, {Component} from 'react';
import Axios from 'axios';
class Login extends Component{

    reg = new RegExp("^[a-z0-9]+([._\\-]*[a-z0-9])*@([a-z0-9]+[-a-z0-9]*[a-z0-9]+.){1,63}[a-z0-9]+$");
    state = {
        password: "",
        email: "",
    }

    emailHandler = (e) =>{
        this.setState({
            email: e.target.value
        })
    }

    passwordHandler = (e) =>{
        this.setState({
            password: e.target.value
        })
    }

    submitHandler = () =>{
        if (this.state.email === "" || this.state.password === ""){
            alert("Please fill all required fields !")
        }
        else if (!this.reg.test(this.state.email)){
            alert("Please input a valid email address !")
        }
        else {
            const axios = Axios.create();
            axios.post('https://jsonplaceholder.typicode.com/posts', this.state)
            .then(res => {
                if (res.status === 200 && res.data.match){
                    console.log('redirect to main page');
                }
                else{
                    console.log('redirect to error page');
                }
            })
        }
    }

    render(){
        return (
            <div className="login-container form-container">
                <form action="#">
                    <h1>Sign in</h1>
                    <div className="social-container">
                        <a href="#" className="social"><i className="fa fa-facebook-official"></i></a>
                        <a href="#" className="social"><i className="fa fa-google"></i></a>
                        <a href="#" className="social"><i className="fa fa-github"></i></a>
                    </div>
                    <span>Sign in with your email</span>
                    <input type="email" placeholder="Email" onChange={this.emailHandler}/>
                    <input type="password" placeholder="Password" onChange={this.passwordHandler}/>
                    <button onClick={this.submitHandler}>Sign In</button>
                </form>
            </div>
        );
    }
}

export default Login;
