import React, {useState} from 'react';
import TopBar from '../Components/MainPage/TopBar';
import Container from '@material-ui/core/Container';
import { makeStyles } from '@material-ui/core/styles';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import InputLabel from '@material-ui/core/InputLabel';

const useStyle = makeStyles(theme =>({
    footer: {
        height: '8vh',
        width: '100vw',
        textAlign: 'center',
        alignItems: 'center',
        backgroundColor: '#fa877f',
        color: '#FFFFFF',
        fontSize: '2vh',
        display :'fixed',
        bottom: '0px'
    },
    solid: {
        display: 'block',
        width: '100%',
        height: '64px',
        backgroundColor: '#FFFFFF'
    },
    container:{
        height: '90vh',
        overflow: 'scroll',
        flexDirection:'column',
        maxWidth: '700px',
    },
    textfield:{
        display: 'flex',
        width: '100%',
        height: '50vh',
        border: 'solid #cccccc 2px',
        borderRadius: '5px',
        [theme.breakpoints.down('md')]:{
            height:'40vh'
        }
    }
}))

const ReviewPage = (props) => {
    const classes = useStyle();
    const [logged, setLog] = useState(true);
    const [star, setStar] = useState(5);
    return (
        <div>
            <TopBar logged={logged}/>
            <div className={classes.solid}></div>
            <Container className={classes.container}>
                <h1>Write a review</h1>
                <div className={classes.textfield}>
                    <FormControl>
                        <InputLabel>Stars</InputLabel>
                        <Select value={star} onChange={onChangeHandler}>
                            <MenuItem value={0}>0</MenuItem>
                            <MenuItem value={1}>1</MenuItem>
                            <MenuItem value={2}>2</MenuItem>
                            <MenuItem value={3}>3</MenuItem>
                            <MenuItem value={4}>4</MenuItem>
                            <MenuItem value={5}>5</MenuItem>
                        </Select>
                    </FormControl>
                </div>
            </Container>
            <div className={classes.footer}>
                <p>Copyright © 2019-03-12 Yummy Inc.</p>
            </div>
        </div>
    )
}

export default ReviewPage;
