import React, { useEffect, useState, useRef } from 'react';
import {useParams} from 'react-router';
import TopBar from '../Components/MainPage/TopBar';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Ratings from '../Components/DetailPage/Rating';
import Button from '@material-ui/core/Button';
import Axios from 'axios';

const axios = Axios.create()

const useStyle = makeStyles(theme => ({
    footer: {
        height: '8vh',
        width: '100vw',
        textAlign: 'center',
        alignItems: 'center',
        backgroundColor: '#fa877f',
        color: '#FFFFFF',
        fontSize: '2vh',
        display: 'fixed',
        bottom: '0px'
    },
    solid: {
        display: 'block',
        width: '100%',
        height: '64px',
        backgroundColor: '#FFFFFF'
    },
    container: {
        height: '90vh',
        overflow: 'scroll',
        flexDirection: 'column',
        maxWidth: '700px',
    },
    userTitle: {
        display: 'flex',
        width: '100%',
        height: '15vh',
        [theme.breakpoints.up('sm')]:{
            height: '18vh'
        },
        color: '#5c5757',
        backgroundColor: '#f4f4f4',
        flexDirection: 'row',
    },
    userInfo: {
        paddingLeft: '5%',
        paddingTop: '2%',
        margin: '0px'
    },
    partition1: {
        display: 'flex',
        flexDirection: 'column',
        width: '70%',
    },
    partition2:{
        display: 'flex',
        flexDirection: 'column',
        width: '30%',
        background: ''
    },
    img:{
        margin: "0px"
    },
    review:{
        width: '100%',
        height: 'auto',
        backgroundColor:'#f4f4f4',
        boxShadow: '5px 5px 10px -5px #aaaaaa',
        paddingBottom:'5px',
        marginTop:'20px'
    },
    reviewTitle:{
        fontWeight:'bold',
        fontSize:'23px',
        paddingTop: '10px',
        margin: '5px'
    },
}))

const UserProfilePage = (props) =>{
    const [img, setImg] = useState(null)
    const [user, setUser] = useState(null)
    let { userId } = useParams();

    useEffect(() => {
        axios.get("https://randomuser.me/api/")
            .then(res => {
                setImg(res.data.results[0].picture.large)
            })
            .then(res => {
                axios.get(`https://midi-fiction.glitch.me/api/userId/${userId}`)
            .then((res => {
                setUser(res.data[0])
                console.log(res.data[0])
                console.log(user)
            }))
            })
    }, [])

    const onDeleteHandler = (e) =>{
        e.preventDefault();
    }

    const classes = useStyle()
    return (
        <div>
            <TopBar userId={userId}/>
            <div className={classes.solid}></div>
            <br />
            <Container className={classes.container}>
                <div className={classes.userTitle}>
                    <div className={classes.partition2}>
                        <img src={img} className={classes.img} height="100%"/>
                    </div>
                    <div className={classes.partition1}>
                        <h1 className={classes.userInfo}>John Doe</h1>
                        <small className={classes.userInfo}>yumming since: 2019-10-09</small>
                    </div>
                </div>
                <h2>My reviews</h2>
                {
                [{name:'John Doe', 
                stars: 4, 
                date: "2016-03-09", 
                "text": "Great place to hang out after work: the prices are decent, and the ambience is fun. It's a bit loud, but very lively. The staff is friendly, and the food is good. They have a good selection of drinks."
                },{name:'John Doe', 
                stars: 4, 
                date: "2016-03-09", 
                "text": "Great place to hang out after work: the prices are decent, and the ambience is fun. It's a bit loud, but very lively. The staff is friendly, and the food is good. They have a good selection of drinks."
                }].map(r => (
                    <div className={classes.review}>
                        <Container>
                            <h3 className={classes.reviewTitle}>{r.name}</h3>
                            <Ratings star='4' size='small'/>
                            <small>{`posted on ${r.date}`}</small>
                            <p>{r.text}</p>
                            <Button color='secondary' onClick={onDeleteHandler}>Delete Review</Button>
                        </Container>
                    </div>
                ))
            }
            </Container>
            <div className={classes.footer}>
                <p>Copyright © 2019-03-12 Yummy Inc.</p>
            </div>
        </div>
    )
}

export default UserProfilePage;
