import React from 'react';
import { fade, makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import InputBase from '@material-ui/core/InputBase';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },
  AppBar: {
    backgroundColor: '#fa877f',
  },
  title: {
    flexGrow: 1,
    display: 'none',
    fontFamily: 'Pacifico, cursive',
    fontSize: '30px',
    [theme.breakpoints.up('sm')]: {
      display: 'block',
    },
  },
  search: {
    position: 'relative',
    borderRadius: '2px',
    backgroundColor: fade('#FFFFFF', 0.3),
    '&:hover': {
      backgroundColor: fade('#FFFFFF', 0.3),
    },
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing(1),
      width: 'auto',
    },
  },
  searchIcon: {
    width: theme.spacing(7),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  inputRoot: {
    color: 'inherit',
  },
  inputInput: {
    color: '#FFFFFF',
    padding: theme.spacing(1, 1, 1, 7),

    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      width: 250,
      '&:focus': {
        width: 300,
      },
    },
  },
  Button:{
      display: 'none',
      [theme.breakpoints.up('sm')]:{
          marginLeft: '1%',
          display: 'block',
          fontSize: '18px'
      }
  },
  MenuButton:{
      color: '#FFFFFF',
      display: 'block',
       [theme.breakpoints.up('sm')]:{
           display: 'none',
       }
  }
}));

const TopBar = () => {
  const classes = useStyles();

  // Handle Search Event
  const SearchHandler = (e) => {
    if (e.keyCode == 13){
        console.log(e.target.value);
    }
  }

  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClick = event => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <div className={classes.root}>
      <AppBar position="fixed" className={classes.AppBar}>
        <Toolbar>
          <Typography className={classes.title}>
            Yummy
          </Typography>
          <div className={classes.search}>
            <div className={classes.searchIcon}>
              <SearchIcon />
            </div>
            <InputBase 
              onKeyDown={SearchHandler}
              placeholder="Search for your interests..."
              classes={{
                root: classes.inputRoot,
                input: classes.inputInput,
              }}
            />
          </div>
          <IconButton className={classes.MenuButton} onClick={handleClick}>
              <MenuIcon/>
          </IconButton>
          <Menu 
            anchorEl={anchorEl}
            keepMounted
            open={Boolean(anchorEl)}
            onClose={handleClose}>
                <MenuItem onClick={handleClose}>Sign Up</MenuItem>
                <MenuItem onClick={handleClose}>Log in</MenuItem>
          </Menu>
          <Button color="inherit" className={classes.Button}>Login</Button>
          <Button color="inherit" className={classes.Button}>Signup</Button>
        </Toolbar>
      </AppBar>
    </div>
  );
}
export default TopBar;
