import React from 'react';
import Container from '@material-ui/core/Container';
import { makeStyles } from '@material-ui/core/styles';
import BusinessTitle from './BusinessTitle';
import Button from '@material-ui/core/Button';
import Gallery from '../MainPage/Gallery';
import Link from '@material-ui/core/Link';

const useStyle = makeStyles(theme =>({
    container:{
        height: '90vh',
        overflow: 'scroll',
        flexDirection:'column',
        maxWidth: '700px',
    },
    h3:{
        color: '#5c5757'
    },
    review:{
        width: '100%',
        height: '30vh',
        marginTop: '10px'
    }
}))

const ReviewList = (props) => {
    const classes = useStyle()
    const hours = {
        "Monday": "10:00-21:00",
        "Tuesday": "10:00-21:00",
        "Friday": "10:00-21:00",
        "Wednesday": "10:00-21:00",
        "Thursday": "10:00-21:00",
        "Sunday": "11:00-18:00",
        "Saturday": "10:00-21:00",
    }
    return (
        <Container className={classes.container}>
            <BusinessTitle name='Coconuts Caribbean Restaurant & Bar' 
            reviews='9710 reviews'
            address='475 3rd St'
            city='San Francisco'
            state='CA'
            postal='94107'
            hours={hours}/>
            <br/>
            <h3 className={classes.h3}>Recommended Reviews</h3>
            <Button variant='contained' color='secondary'>Write a review</Button>
            <br/>
            {
                [{name:'John Doe', 
                stars: 4, 
                date: "2016-03-09", 
                "text": "Great place to hang out after work: the prices are decent, and the ambience is fun. It's a bit loud, but very lively. The staff is friendly, and the food is good. They have a good selection of drinks."
                }].map(r => (
                    <div className={classes.review}>

                    </div>
                ))
            }
        </Container>
    )
}

export default ReviewList;
